/**
 * @param {string} s
 * @return {string}
 */
var longestPalindrome = function(s) {
    let cutLength,
        cutStr,
        startIndex;
    for (let cutLength = s.length; cutLength >= 1; cutLength --) {
        for (let startIndex = 0; startIndex <= s.length - cutLength; startIndex ++) {
            cutStr = s.substr(startIndex, cutLength);
            if(isPalindrome(cutStr)) {
                return cutStr;
            }
        }
    }
    return "";
};

var isPalindrome = function(s) {
    return s === s.split("").reverse().join("");
};

var str = "";

console.log(longestPalindrome(str));

